let connectedUserSocketId;
let peerConnection;
let dataChannel;
let screenSharingStream;

//Get Camera
export const getLocalStream = () => {
  navigator.mediaDevices
    .getUserMedia(defaultConstrains)
    .then((stream) => {
      store.dispatch(setLocalStreamInitiate(stream));
      store.dispatch(setCallStateInitiate({ call: callStates.CALL_AVAILABLE }));
      // createPeerConnection();
    })
    .catch((err) => {
      console.log(
        "error occured when trying to get an access to get local stream"
      );
      console.log(err);
    });
};
//Call
export const handlePreOffer = (data) => {
  if (checkIfCallIsPossible()) {
    connectedUserSocketId = data.callerSocketId;
    store.dispatch(setCallerUsernameInitiate(data.callerUsername));
    store.dispatch(setCallStateInitiate({ call: callStates.CALL_REQUESTED }));
    console.log(data, "========1");
  } else {
    wss.sendPreOfferAnswer({
      callerSocketId: data.callerSocketId,
      answer: preOfferAnswers.CALL_NOT_AVAILABLE,
    });
  }
};
//accept call
export const acceptIncomingCallRequest = () => {
  wss.sendPreOfferAnswer({
    callerSocketId: connectedUserSocketId,
    answer: preOfferAnswers.CALL_ACCEPTED,
  });

  store.dispatch(setCallStateInitiate({ call: callStates.CALL_IN_PROGRESS }));
};
//reject call
export const rejectIncomingCallRequest = () => {
  wss.sendPreOfferAnswer({
    callerSocketId: connectedUserSocketId,
    answer: preOfferAnswers.CALL_REJECTED,
  });
  resetCallData();
};
//call

export const handlePreOfferAnswer = (data) => {
  store.dispatch(setCallingDialogVisibleInitiate(false));

  if (data.answer === preOfferAnswers.CALL_ACCEPTED) {
    sendOffer();
  } else {
    let rejectionReason;
    if (data.answer === preOfferAnswers.CALL_NOT_AVAILABLE) {
      rejectionReason = "Callee is not able to pick up the call right now";
    } else {
      rejectionReason = "Call rejected by the callee";
    }
    store.dispatch(
      setCallRejectedInitiate({
        rejected: true,
        reason: rejectionReason,
      })
    );

    resetCallData();
  }
};
export const callToOtherUser = (calleeDetails) => {
  console.log(calleeDetails, "=----asdasdas====");
  connectedUserSocketId = calleeDetails.socketId;
  store.dispatch(setCallerUsernameInitiate(callStates.CALL_IN_PROGRESS));
  store.dispatch(setCallingDialogVisibleInitiate({ data: true }));

  wss.sendPreOffer({
    callee: calleeDetails,
    caller: {
      username: store.getState().user.username,
    },
  });
};
export const checkIfCallIsPossible = () => {
  console.log(store.getState().call.localStream, "oke");
  if (
    store.getState().call.localStream === null ||
    store.getState().call.callState !== callStates.CALL_AVAILABLE
  ) {
    return false;
  } else {
    return true;
  }
};
//Function Reject
export const resetCallData = () => {
  connectedUserSocketId = null;
  store.dispatch(setCallStateInitiate(callStates.CALL_AVAILABLE));
};
//show dialog
export const hangUp = () => {
  wss.sendUserHangedUp({
    connectedUserSocketId: connectedUserSocketId,
  });

  resetCallDataAfterHangUp();
};
const resetCallDataAfterHangUp = () => {
  peerConnection.close();
  peerConnection = null;
  createPeerConnection();
  resetCallData();

  const localStream = store.getState().call.localStream;
  localStream.getVideoTracks()[0].enabled = true;
  localStream.getAudioTracks()[0].enabled = true;

  if (store.getState().call.screenSharingActive) {
    screenSharingStream.getTracks().forEach((track) => {
      track.stop();
    });
  }

  store.dispatch(resetCallDataStateInitiate());
};

const createPeerConnection = () => {
  peerConnection = new RTCPeerConnection(configuration);

  const localStream = store.getState().call.localStream;

