import { useDispatch } from "react-redux";
import { setLocalStreamInitiate } from "../../Redux/Action/ActionCall";
import {
  preOfferAnswers,
  defaultConstrains,
  configuration,
} from "../ShareData";

let connectedUserSocketId;
let peerConnection;
let dataChannel;
const dispatch = useDispatch();

export const getLocalStream = () => {
  navigator.mediaDevices
    .getUserMedia(defaultConstrains)
    .then((stream) => {
      dispatch(setLocalStreamInitiate(stream));
      // dispatch(setCallState(callStates.CALL_AVAILABLE));
      // createPeerConnection();
    })
    .catch((err) => {
      console.log(
        "error occured when trying to get an access to get local stream"
      );
      console.log(err);
    });
};
