import {
  setCallStateInitiate,
  setLocalStreamInitiate,
} from "../../Redux/Action/ActionCall";
import {
  preOfferAnswers,
  defaultConstrains,
  configuration,
  callStates,
} from "../ShareData";
import store from "../../Redux/Store";

let connectedUserSocketId;
let peerConnection;
let dataChannel;

export const getLocalStream = () => {
  navigator.mediaDevices
    .getUserMedia(defaultConstrains)
    .then((stream) => {
      dispatch(setLocalStreamInitiate(stream));
      dispatch(setCallStateInitiate(callStates.CALL_AVAILABLE));
      // createPeerConnection();
    })
    .catch((err) => {
      console.log(
        "error occured when trying to get an access to get local stream"
      );
      console.log(err);
    });
};
