import {
  setCallerUsernameInitiate,
  setCallStateInitiate,
  setLocalStreamInitiate,
} from "../../Redux/Action/ActionCall";
import {
  preOfferAnswers,
  defaultConstrains,
  configuration,
  callStates,
} from "../ShareData";
import store from "../../Redux/Store";
import * as wss from "../Connect-Soket/wssConnection";
let connectedUserSocketId;
let peerConnection;
let dataChannel;
//Get Camera
export const getLocalStream = () => {
  navigator.mediaDevices
    .getUserMedia(defaultConstrains)
    .then((stream) => {
      store.dispatch(setLocalStreamInitiate(stream));
      store.dispatch(setCallStateInitiate(callStates.CALL_AVAILABLE));
      // createPeerConnection();
    })
    .catch((err) => {
      console.log(
        "error occured when trying to get an access to get local stream"
      );
      console.log(err);
    });
};
export const handlePreOffer = (data) => {
  if (checkIfCallIsPossible()) {
    connectedUserSocketId = data.callerSocketId;
    // store.dispatch(setCallerUsername(data.callerUsername));
    // store.dispatch(setCallState(callStates.CALL_REQUESTED));
  } else {
    wss.sendPreOfferAnswer({
      callerSocketId: data.callerSocketId,
      answer: preOfferAnswers.CALL_NOT_AVAILABLE,
    });
  }
};
export const callToOtherUser = (calleeDetails) => {
  connectedUserSocketId = calleeDetails.socketId;
  store.dispatch(setCallerUsernameInitiate(callStates.CALL_IN_PROGRESS));
  // store.dispatch(setCallingDialogVisible(true));
  // wss.sendPreOffer({
  //   callee: calleeDetails,
  //   caller: {
  //     username: store.getState().dashboard.username,
  //   },
  // });
};
export const checkIfCallIsPossible = () => {
  if (
    store.getState().call.localStream === null ||
    store.getState().call.callState !== callStates.CALL_AVAILABLE
  ) {
    return false;
  } else {
    return true;
  }
};
